const ALBUMS_URL="https://jsonplaceholder.typicode.com/albums",PHOTOS_URL="https://jsonplaceholder.typicode.com/photos?albumId={{id}}",ALBUM_ITEM_CLASS="album-item",albumsEl=document.querySelector("#album"),photosEl=document.querySelector("#photo"),albumItemTemplate=document.querySelector("#albumItem").innerHTML,photoItemTemplate=document.querySelector("#photoItem").innerHTML;function init(){getAlbums().then(getFirstPhotos)}function getAlbums(){return fetch(ALBUMS_URL).then(t=>t.json()).then(t=>(renderAlbums(t),t))}function renderAlbums(t){albumsEl.innerHTML=t.map(t=>createAlbum(t)).join("\n")}function createAlbum(t){return albumItemTemplate.replace("{{id}}",t.id).replace("{{title}}",t.title)}function getFirstPhotos(t){t.length&&getPhotos(t[0].id)}function getPhotos(t){return fetch(getPhotoUrl(t)).then(t=>t.json()).then(renderPhotos)}function getPhotoUrl(t){return PHOTOS_URL.replace("{{id}}",t)}function renderPhotos(t){photosEl.innerHTML=t.map(t=>createPhoto(t)).join("\n")}function createPhoto(t){return photoItemTemplate.replace("{{url}}",t.thumbnailUrl).replace("{{title}}",t.title)}function onClick(t){t.target.classList.contains("album-item")&&getPhotos(t.target.dataset.id)}albumsEl.addEventListener("click",onClick),init();