async function render(){let t=await fetch("https://5dd3d5ba8b5e080014dc4bfa.mockapi.io/students",{method:"GET"}),e=await t.json();const n=document.getElementById("table-students");let d="<tr>\n                                    <th>Имя студента</th>\n                                    <th>Д3 1</th>\n                                    <th>Д3 2</th>\n                                    <th>Д3 3</th>\n                                    <th>Д3 4</th>\n                                    <th>ДЗ 5</th>\n                                    <th>Д3 6</th>\n                                    <th>Д3 7</th>\n                                    <th>Д3 8</th>\n                                    <th>Д3 9</th>\n                                    <th>Д3 10</th>\n                                    <th>Настройка</th>\n                                  </tr>";e.forEach((function(t,e,n){let a=t.id,s=t.name,o=t.marks;d+=`<tr><td class="name-student">${s}</td>`,o.forEach((function(t,e,n){console.log("mark"+e,t),d+=`<td><input type="text" class="mark-input" value="${t}" onblur="updateStudent(event, ${a})"></td>`})),d+=`<td>\n                              \n                                <span onclick="deleteStudents(event, ${a})">X</span>\n                        \n                            </td>\n                          </tr>\n                      `})),n.innerHTML=d}async function deleteStudents(t,e){t.preventDefault();await fetch("https://5dd3d5ba8b5e080014dc4bfa.mockapi.io/students/"+e,{method:"DELETE"});render()}async function updateStudent(t,e){let n=t.target.closest("tr").querySelectorAll("input"),d=[];Array.prototype.forEach.call(n,(function(t){d.push(t.value)}));let a={marks:d};await fetch("https://5dd3d5ba8b5e080014dc4bfa.mockapi.io/students/"+e,{method:"PUT",headers:{"Content-Type":"application/json;charset=utf-8"},body:JSON.stringify(a)});render()}document.addEventListener("DOMContentLoaded",render);let insertStudent=document.getElementById("insert-student"),insert